import apiAndAtomRelation from '../../../api/apiInfo/apiAndAtomRelation.js'
import CRUD, { presenter, header, form, crud } from '@crud/crud'
import rrOperation from '@crud/RR.operation'
import crudOperation from '@crud/CRUD.operation'
import udOperation from '@crud/UD.operation'
import pagination from '@crud/Pagination'
import {getData2Api} from '../../../api/indicators/indIndicatorManual'
import Treeselect from '@riophae/vue-treeselect'
import '@riophae/vue-treeselect/dist/vue-treeselect.css'
// crud交由presenter持有
const defaultCrud = CRUD({ title: '指标授权', url: apiAndAtomRelation.url, sort: 'id,desc', crudMethod: { ...apiAndAtomRelation.crud }})
const defaultForm = {
  id: null,
  apiId: null,
  bizName: null,
  bizCode: null,
  crtUserCode: null,
  crtOrgCode: null,
  crtDate: null,
  updUserCode: null,
  updOrgCode: null,
  updDate: null }
  const bizNames={}
  const depts={}
export default {
  name: 'apiAndAtomRelation',
  props:["msg"],
  components: { Treeselect,pagination, crudOperation, rrOperation, udOperation },
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  dicts: ['IND_CURRENCY', 'IE_FREQUENCY'],

  data() {
    return {
      bizNames,
      permission: {
        add: ['admin', 'resultCheck:add'],
        edit: ['admin', 'resultCheck:edit'],
        del: ['admin', 'resultCheck:del']
      },
      rules: {
        bizName: [
          { required: true, message: '指标名称不能为空', trigger: 'blur' }
        ],
      },
    }
  },
  created() {
   this.form.apiId = this.msg
    console.log(this.form.apiId)
  },
  mounted() {
    getData2Api().then(data => {
      this.bizNames = data;
      console.log("----------"+data)
    })
  },
  methods: {
    // 获取数据前设置好接口地址
    [CRUD.HOOK.beforeRefresh]() {
      this.query.apiId = this.msg
      this.crud.optShow.del = true
      this.crud.optShow.add = true
      this.crud.optShow.edit = true
      return true
    },
    [CRUD.HOOK.beforeToAdd](){
      this.form.apiId = this.msg
    },
    forceUpdate(e){
      this.$forceUpdate(); //
    },
    closeDialog(){
      this.interfaceDialog = false;

    },

  }
}
