import crud,{beforeSubmitMethod} from '@eladmin/mixins/crud'
import dictDetail from './dictDetail'
import crudDict,{getDicts} from '../../../api/system/dict'
export default {
  name: 'Dict',
  components: { dictDetail },
  mixins: [crud],
  data() {
    return {
      title: '参数',
      allDate:[],
      crudMethod: { ...crudDict },
      queryTypeOptions: [
        { key: 'name', display_name: '参数名称' },
        { key: 'remark', display_name: '描述' }
      ],
      form: { id: null, name: null, remark: null },
      rules: {
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ]
      }
    }
  },
  created() {
    this.$nextTick(() => {
      this.init()
    })
  },
  methods: {
    // 获取数据前设置好接口地址
    beforeInit() {
      this.url = 'api/dict'
      if (this.$refs.dictDetail) {
        this.$refs.dictDetail.data = []
        this.$refs.dictDetail.dictName = ''
      }
      return true
    },
    beforeSubmitMethod(){
      if(this.form.id){
        for(var j=0;j<Object.keys(this.allDate).length;j++){
          if(this.form.id == this.allDate[j].id){
            this.allDate.splice(j,1)
            break;
          }
        }
      }
      for(var i=0;i<Object.keys(this.allDate).length;i++){
        if(this.form.name == this.allDate[i].name){
          this.$message({
            message: '参数名称不可以重复！',
            type: 'error'
          });
          return false;
        }
      }
      return true;
    },
    afterEditMethod(){
      getDicts().then(data =>{
        this.allDate = data
      })
    },
    afterDelMethod(){
      getDicts().then(data =>{
        this.allDate = data
      })
    },
    // 选中参数后，设置参数详情数据
    handleCurrentChange(val) {
      if (val) {
        this.$refs.dictDetail.dictName = val.name
        this.$refs.dictDetail.form.dict.id = val.id
        this.$refs.dictDetail.init()
      }
    }
  },
  mounted() {
    getDicts().then(data =>{
      this.allDate = data
    })
  }
}
