import crudJob from '../../../api/system/job'
import eHeader from './module/header'
import eForm from './module/form'
import CRUD, { presenter, header, form, crud } from '@crud/crud'
import crudOperation from '@crud/CRUD.operation'
import pagination from '@crud/Pagination'
import udOperation from '@crud/UD.operation'
import { mapGetters } from 'vuex'
import request from '@eladmin/utils/request'
// crud交由presenter持有
const defaultCrud = CRUD({title: '岗位',url: 'api/job',crudMethod: { ...crudJob }})

export default {
  name: 'Job',
  components: { eHeader, eForm, crudOperation, pagination, udOperation },
  mixins: [presenter(defaultCrud), header(),crud()],
  // 数据字典
  dicts: ['job_status'],
  data() {
    return {
      permission: {
        add: ['admin', 'job:add'],
        edit: ['admin', 'job:edit'],
        del: ['admin', 'job:del']
      }
    }
  },
  computed: {
    ...mapGetters([
      'job'
    ])
  },
  created() {
    // console.log("初始化查询表单传输数据！" + this.form.enabled)
    //this.query.enabled = 'true'
    //this.crud.refresh()
    this.$nextTick(() => {
      //this.getDeptDatas()
      this.crud.toQuery()
      this.crud.msg.add = '新增成功，默认密码：123456'
    })
    this.resetQuery()
  },
  mounted: function() {
    const that = this
    window.onresize = function temp() {
      that.height = document.documentElement.clientHeight - 180 + 'px;'
    }
  },
  methods: {
    resetQuery(){
      this.crud.resetQuery(false);
      //this.query.enabled = 'true'
      this.crud.refresh()
    },
    // 改变状态
    changeEnabled(data, val) {
      this.$confirm('此操作将 "' + this.dict.label.job_status[val] + '" ' + data.name + '岗位, 是否继续？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        crud.crudMethod.edit(data).then(() => {
          crud.notify(this.dict.label.job_status[val] + '成功', 'success')
        }).catch(err => {
          data.enabled = !data.enabled
          console.log(err.data.message)
        })
      }).catch(() => {
        data.enabled = !data.enabled
      })
    }
  }
}
